# Helper function to create question
def create_question(domain, difficulty, discrimination, text_ar, options, correct, explanation_ar, cultural_context):
    return {
        "id": str(uuid.uuid4()),
        "domain": domain,
        "difficulty": difficulty,
        "discrimination": discrimination,
        "guessing": 0.25,
        "text_ar": text_ar,
        "options": options,
        "correct": correct,
        "explanation_ar": explanation_ar,
        "culturalContext": cultural_context
    }

